import React from 'react';

function SearchForm(props) {
  return (
    <div
      className="form-group"
      style={{
        display: 'flex',
        flexFlow: 'row',
        padding: '10px',
        width: '100%',
      }}
    >
      <label className="search">Search</label>
      <input
        onChange={props.handleInputChange}
        value={props.search}
        name="search"
        type="text"
        className="form-control"
        placeholder="Search"
        id="search"
      />
    </div>
  );
}

export default SearchForm;
