import React from 'react';
import API from '../../utils/API';
import Employees from '../EmployeeCard';
import SearchForm from '../SearchForm';

class Results extends React.Component {
  state = {
    employees: [],
    filter: '',
    sortType: '',
  };

  componentDidMount() {
    this.getEmployees();
    console.log(this.state.sortType);
  }

  getEmployees = () => {
    API.getEmployees()
      .then((res) => {
        this.setState({
          employees: res.data.results,
        });
        console.log(this.state.employees);
      })
      .catch((err) => console.log(err));
  };

  sortEmployees = (sortFunction) => {
    console.log('in sort');
    const sortedEmployees = this.state.employees.sort(sortFunction);
    this.setState({
      employees: sortedEmployees,
    });
  };

 filterEmployees = (searchkey) => {
    console.log('in search');
    console.log(this.state.employees);
    var filterResult = this.state.employees.filter(
      (employee) => employee.firstName === searchkey
    );

    this.setState({
      employees: filterResult,
    });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    const value = event.target.value;
    const name = event.target.name;
    console.log('form submitted');
    console.log(value);
    console.log(name);

    this.filterEmployees(value);
    this.setState({
      [name]: value,
    });
    this.filterEmployees(value);
    this.filterEmployees(this.state.search);
  };

  handleInputChange = (event) => {
    event.preventDefault();
    console.log(event);
    const value = event.target.value;
    const name = event.target.name;
    console.log('input');

    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div className="container">
        <div>
          <SearchForm
            value={this.state.search}
            handleInputChange={this.handleInputChange}
            handleFormSubmit={this.handleFormSubmit}
          />
        </div>
        <div className="datatable mt-5">
          <table
            id="table"
            className="table table-striped table-hover table-condensed"
          >
            <thead>
              <tr>
                <th>Image</th>
                <th
                  onClick={() =>
                    this.sortEmployees((a, b) =>
                      a.name.first.localeCompare(b.name.first)
                    )
                  }
                >
                  Name
                </th>
                <th>Phone</th>
                <th>Email</th>
                <th
                  onClick={() =>
                    this.sortEmployees((a, b) =>
                      a.dob.date.localeCompare(b.dob.date)
                    )
                  }
                >
                  DOB
                </th>
              </tr>
            </thead>
            <Employees
              sort={this.state.sortType}
              employees={this.state.employees}
            />
          </table>
        </div>
      </div>
    );
  }
}
export default Results;
