import React from 'react';
import API from '../../utils/API';
import Employees from '../EmployeeCard';

class Results extends React.Component {
  state = {
    employees: [],
    search: '',
    sortType: '',
  };

  componentDidMount() {
    this.getEmployees();
    console.log(this.state.sortType);
  }

  getEmployees = () => {
    API.getEmployees()
      .then((res) => {
        this.setState({
          employees: res.data.results,
        });
        console.log(this.state.employees);
      })
      .catch((err) => console.log(err));
  };

  sortEmployees = (sortFunction) => {
    console.log("in sort")
    const sortedEmployees = this.state.employees.sort(sortFunction);
    this.setState({
      employees: sortedEmployees
    })
  }

  render() {
    return (
      <div className="container">
        <div className="datatable mt-5">
          <table
            id="table"
            className="table table-striped table-hover table-condensed"
          >
            <thead>
              <tr>
                <th>Image</th>
                <th onClick={() => this.sortEmployees((a, b) => a.name.first.localeCompare(b.name.first))}>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th onClick={() => this.sortEmployees((a, b) => a.dob.date < b.dob.date)}>DOB</th>
              </tr>
            </thead>
            <Employees
              sort={this.state.sortType}
              employees={this.state.employees}
            />
          </table>
        </div>
      </div>
    );
  }
}
export default Results;
